"use strict";

(function () {
  var filterItem = document.querySelectorAll('.filter__item');
  filterItem.forEach(function (block) {
    block.addEventListener('click', function () {
      if (block.classList.contains('active')) {
        block.classList.remove('active');
      } else {
        filterItem.forEach(function (item) {
          return item.classList.remove('active');
        });
        block.classList.add('active');
      }
    });
  });
  var newsContainer = document.querySelector('.news');
  var newsItems = newsContainer.querySelectorAll('.news__item');
  var maxVisible = 6;
  var currentIndex = maxVisible;
  var step = 3; // Кількість новин, які показуються за один клік (1-3)

  if (newsItems.length > maxVisible) {
    newsItems.forEach(function (item, index) {
      if (index >= maxVisible) item.style.display = 'none';
    });
    if (!document.querySelector('.news__show-more-btn')) {
      var btn = document.createElement('button');
      btn.textContent = 'Показати більше';
      btn.className = 'news__show-more-btn';
      newsContainer.after(btn);
      btn.addEventListener('click', function () {
        var nextIndex = Math.min(currentIndex + step, newsItems.length);
        for (var i = currentIndex; i < nextIndex; i++) {
          newsItems[i].style.display = '';
        }
        currentIndex = nextIndex;
        if (currentIndex >= newsItems.length) {
          btn.remove();
        }
      });
    }
  }
})();
